 * @param {MedalDetailData | OldMedalDetailData} medalItem - 点击的勋章数据
 */
const openMedalDetailModal = (medalItem: MedalDetailData | OldMedalDetailData): void => {
  // 获取勋章弹窗所需数据
  historyFlag.value = medalItem.medalType === 'history';
