const showNavigation = computed(() => Application.environment.inTHSApp && !Application.environment.inPC && Application.getAppInfo().appName !== 'thsHD');

onMounted(async () => {
  // 屏幕宽度小于 660px 时，设置勋章墙宽度
  if (document.getElementById('medalWall') !== null) {
    MediaQuery.changeWindowSize(document.getElementById('medalWall') as Element, COMMON_NUMBER.MAX_WIDTH);
  }
  // 监听窗口变化
  window.addEventListener('resize', () => {
    MediaQuery.changeWindowSize(document.getElementById('medalWall') as Element, COMMON_NUMBER.MAX_WIDTH);
  });
  // 初始化客户端监听事件
  Application.initNativeNotifyFunction();
  // 获取勋章墙数据
  await getInitData(User.medalWallUserId, true);
  // 如果有勋章 id，初始展示勋章弹窗
  if (User.medalId) {
    // 初始化勋章弹窗数据
    medalId.value = User.medalId;
    // 初始化勋章历史标识
    historyFlag.value = User.historyFlag;
    // 展示勋章弹窗
    showMedalPopup.value = true;
  }
});

/**
 * 刷新页面
 */
const refreshPage = (): void => {
  // 隐藏错误页面
  PageErrorStatus.value = false;
  // 显示加载中
  loading.value = true;
  // 重新获取勋章墙数据
  getInitData(User.medalWallUserId, true);
};

/**
 * 获取勋章墙数据
 * @param {number} uid - 用户 id
 * @param {boolean} firstLoad - 是否第一次加载
 */
const getInitData = async (uid: number, firstLoad = false): Promise<void> => {
  try {
    // 获取勋章墙数据
    const res = await getMedalData({ uid: uid });
    // 初始化勋章墙数据
    medalWallData.value = res;
    // 初始化新版勋章数据
    newMedalList.value = res.listNew;
    // 初始化老版勋章数据
    oldMedalList.value = res.listOld;
    // 处理自己查看勋章墙时，获取已佩戴和可佩戴的勋章数据，并更新勋章状态
    handleSelfViewMedal();
    // 隐藏加载中
    loading.value = false;
    // 上报展示勋章墙
    window.weblog && window.weblog.report({
      id: 'ths_sns_medalPage',
      action: 'show',
      logmap: {
        showType: User.selfView ? 'my' : 'visitor',
      },
    });
  } catch {
    if (firstLoad) {
      // 隐藏加载中
      loading.value = false;
      // 第一次加载页面显示错误页面
      PageErrorStatus.value = firstLoad;
    } else {
      Toast('已佩戴勋章更新失败');
    }
  }
};

/**
 * 处理自己查看勋章墙时，获取已佩戴和可佩戴的勋章数据，并更新勋章状态
 */
const handleSelfViewMedal = (): void => {
  // 自己查看勋章墙时，才获取已佩戴和可佩戴的勋章数据，并更新勋章状态
  if (User.selfView) {
    // 重置可佩戴的勋章数据
    wearMedalList.value = [];
    // 重置已佩戴的勋章数据
    wearedMedalList.value = [];
    // 获取已佩戴和可佩戴的勋章数据
    newMedalList.value.forEach((categoryItem: NewMedalList) => {
      categoryItem?.medalSeriesList.forEach((medalItem: MedalDetailData) => {
        // 已佩戴的勋章和有可佩戴的勋章，分别存储
        if (medalItem.wearMedal) {
          wearedMedalList.value.push(medalItem);
        } else {
          // 已有的勋章，并且未过期，才能佩戴
          const medalUnexpired = medalItem?.expirationTime * COMMON_NUMBER.SECOND > new Date().getTime() || medalItem?.expirationTime === 0;
          if (medalItem.hasMedal && medalUnexpired) {
            wearMedalList.value.push(medalItem);
          }
        }
        // 新获得的勋章，需要更新勋章状态
        medalItem.newAcquired && updateMedalList.value.push(medalItem.medalId);
      });
    });
    // 更新勋章状态
    updateMedalStatus();
  }
};

/**
 * 更新勋章状态
 */
const updateMedalStatus = () => {
  // 没有新获得的勋章，不需要更新
  if (!updateMedalList.value.length) {
    return;
  }
  // 更新勋章状态
  try {
    updateMedal({
      uid: userid,
      medalIdList: updateMedalList.value.join(','),
    });
  } catch (error) {
    return;
  }
};

/**
 * 处理佩戴勋章弹窗展示和隐藏
 * @param {boolean} showStatus - 是否展示佩戴勋章弹窗
 */
const handleWearMedalPop = (showStatus: boolean): void => {
  if (showStatus) {
    window.medalScrollTop = document.documentElement.scrollTop || document.body.scrollTop;
    // 给 body 添加样式，防止滚动
    document.body.style.position = 'fixed';
    document.body.style.top = `-${window.medalScrollTop}px`;
  } else {
    // 恢复滚动
    document.body.style.position = '';
    document.body.style.top = '';
    // 恢复滚动位置
    document.documentElement.scrollTop = document.body.scrollTop = (window.medalScrollTop || 0) as number;
  }
  // 切换佩戴勋章弹窗的展示状态
  showWearMedalModal.value = showStatus;
};

/**
 * 佩戴/取消佩戴勋章
 * @param {MedalDetailData} medalItem - 勋章数据
 */
const handleOperateMedal = throttle(async (wearedMedalData: MedalDetailData[]): Promise<void> => {
  // 获取佩戴/取消佩戴勋章状态
  const action = wearedMedalData.length ? 'wear' : 'remove';
  // 获取佩戴/取消佩戴勋章文案
  const text = action === 'wear' ? '佩戴' : '取消佩戴';
  try {
    // 保存佩戴的勋章
    await operateMedal({
      uid: userid,
      medalId: action === 'wear' ? wearedMedalData[0].medalId : wearedMedalList.value[0].medalId,
      action,
    });
    Toast(`${text}勋章成功`);
    // 关闭佩戴勋章弹窗
    handleWearMedalPop(false);
    // 关闭勋章详情弹窗
    closeMedal();
    // 重新获取勋章墙数据
    getInitData(User.medalWallUserId);
  } catch (error) {
    Toast(`${text}勋章失败`);
  }
}, COMMON_NUMBER.THROTTLE_TIME);

/**
 * 打开勋章详情弹窗
 * @param {MedalDetailData | OldMedalDetailData} medalItem - 点击的勋章数据
 */
const openMedalDetailModal = (medalItem: MedalDetailData | OldMedalDetailData): void => {
  // 获取勋章弹窗所需数据
  historyFlag.value = medalItem.medalType === 'history';
  medalId.value = medalItem.medalId;
  showMedalPopup.value = true;
};

/**
 * 点击勋章弹窗佩戴勋章按钮
 */
const handleWearMedal = (wearedMedalData: MedalDetailData): void => {
  // 端外回流
  if (Application.outSlideReflow()) {
    return;
  }
  if (wearedMedalList.value.length) {
    // 如果已经佩戴了勋章，关闭勋章详情弹窗，展示佩戴勋章弹窗
    handleWearingMedal();
  } else {
    // 佩戴勋章
    handleOperateMedal([wearedMedalData]);
  }
};

/**
 * 点击勋章弹窗佩戴中按钮，关闭勋章详情弹窗，展示佩戴勋章弹窗
 */
const handleWearingMedal = (): void => {
  // 端外回流
  if (Application.outSlideReflow()) {
