  }
  // 切换佩戴勋章弹窗的展示状态
  showWearMedalModal.value = showStatus;
};

/**
 * 佩戴/取消佩戴勋章
 * @param {MedalDetailData} medalItem - 勋章数据
 */
const handleOperateMedal = throttle(async (wearedMedalData: MedalDetailData[]): Promise<void> => {
  // 获取佩戴/取消佩戴勋章状态
  const action = wearedMedalData.length ? 'wear' : 'remove';
  // 获取佩戴/取消佩戴勋章文案
  const text = action === 'wear' ? '佩戴' : '取消佩戴';
  try {
    // 保存佩戴的勋章
    await operateMedal({
      uid: userid,
      medalId: action === 'wear' ? wearedMedalData[0].medalId : wearedMedalList.value[0].medalId,
      action,
    });
    Toast(`${text}勋章成功`);
    // 关闭佩戴勋章弹窗
    handleWearMedalPop(false);
    // 关闭勋章详情弹窗
    closeMedal();
    // 重新获取勋章墙数据
    getInitData(User.medalWallUserId);
  } catch (error) {
    Toast(`${text}勋章失败`);
  }
}, COMMON_NUMBER.THROTTLE_TIME);

/**
 * 打开勋章详情弹窗
 * @param {MedalDetailData | OldMedalDetailData} medalItem - 点击的勋章数据
 */
const openMedalDetailModal = (medalItem: MedalDetailData | OldMedalDetailData): void => {
  // 获取勋章弹窗所需数据
  historyFlag.value = medalItem.medalType === 'history';
  medalId.value = medalItem.medalId;
  showMedalPopup.value = true;
};

/**
 * 点击勋章弹窗佩戴勋章按钮
 */
const handleWearMedal = (wearedMedalData: MedalDetailData): void => {
  // 端外回流
  if (Application.outSlideReflow()) {
    return;
  }
  if (wearedMedalList.value.length) {
    // 如果已经佩戴了勋章，关闭勋章详情弹窗，展示佩戴勋章弹窗
    handleWearingMedal();
  } else {
    // 佩戴勋章
    handleOperateMedal([wearedMedalData]);
  }
};

/**
 * 点击勋章弹窗佩戴中按钮，关闭勋章详情弹窗，展示佩戴勋章弹窗
 */
const handleWearingMedal = (): void => {
  // 端外回流
  if (Application.outSlideReflow()) {
    return;
  }
  // 关闭勋章详情弹窗
  closeMedal();
  // 展示佩戴勋章弹窗
  handleWearMedalPop(true);
};

/**
 * 点击勋章弹窗炫耀一下勋章按钮
 */
const handleShareMedal = throttle((shareElement: HTMLElement): void => {
  // 端外回流
  if (Application.outSlideReflow()) {
    return;
  }
  if (shareMedalStatus.value) {
    return;
  }
  if (!Application.environment.inTHSApp) {
    Toast('暂不支持该功能~');
  }
  // 将分享元素添加到 body 中
  document.body.appendChild(shareElement);
  // 截取勋章分享图片
  html2canvas(document.getElementById('medal-share-container') as HTMLElement, {
    useCORS: true,
    allowTaint: true,
  }).then(cutCanvas => {
    // 分享勋章图片
    Share.shareImage(cutCanvas.toDataURL());
  }).catch(() => {
    Toast('分享失败，请重试');
  }).finally(() => {
    // 截取后，移除分享元素
    document.body.contains(shareElement) && document.body.removeChild(shareElement);
    // 分享状态重置
    shareMedalStatus.value = false;
  });
}, COMMON_NUMBER.THROTTLE_TIME);

/**
 * 关闭勋章详情弹窗
 */
const closeMedal = () => {
  showMedalPopup.value = false;
};
