  computed: {
    // 卡片是否展示全屏
    getFullScreen(): boolean {
      return this.shareData?.displayInfo?.fullScreen;
    },
    // 获取角标文案
    getCornerMark(): boolean {
      return this.shareData?.displayInfo?.cornerMark;
    },
    // 展示内容型插件
    showContentType(): boolean {
      return ['1', '2', '3', '4'].includes(this.shareData.displayInfo.type);
    },
    // 是否展示实盘认证类型卡片
    showCertifyImage(): boolean {
      return ['7'].includes(this.shareData.displayInfo.type);
    },
    // 是否展示图片类型分享
    showImageType(): boolean {
      return ['5', '6'].includes(this.shareData.displayInfo.type);
    },
    // 新画线的兜底措施，因为是已图片形式展示，图片为空时，整个组件就不展示了
    noshowComponent(): boolean {
      return ['6'].includes(this.shareData.displayInfo.type) && !this.shareData?.displayInfo?.image;
    },
    // 是否展示副标题
    showSubTitle(): boolean {
      const { subTitle = '', type } = this.shareData.displayInfo;
      // 类型为 1， 2时才展示副标题
      return ['1', '2'].includes(type) && Boolean(subTitle);
    },
    // 是否展示右侧数据
    showShareData(): boolean {
      return this.showSubTitle && Boolean(this.shareData.displayInfo.data);
    },
    // 获取数据样式
    getDataClass(): string {
      // 赋予默认颜色样式
      let result = 'theme-text-title';
      const reg = /-?\d+(.\d+)?/;
      if (this.shareData?.displayInfo?.data) {
        const data = this.shareData.displayInfo.data.match(reg);
        if (data && data[0]) {
          // 为数据的情况下展示
          if (Number(data[0]) > 0) {
            result = 'share-number_inc';
            this.data = '+' + this.shareData.displayInfo.data;
          } else if (Number(data[0]) < 0) {
            result = 'share-number_des';
          }
        }
      }
      return result;
    },
    // 获取标题样式
    getTitleClass(): string {
      let result = '';
      // 这两种类型的卡片标题栏为两行
      const titleArray = ['3', '4'];
      if (titleArray.includes(this.shareData.displayInfo.type)) {
        result = 'share-title_lines';
      }
      return result;
    },
  },
  methods: {
    // 裂图处理
    handleImageError(e): void {
      this.imageUrl = '';
      e.target.onerror = null;
    },
    // 获取图片数据
    getImage(): void {
      if (this.shareData?.displayInfo?.image) {
        this.imageUrl =
          Application.themeMode === 'white'
            ? this.shareData.displayInfo.image
            : this.shareData.displayInfo.imageNight || '';
      }
    },
    // 跳转地址
    jumpUrl(event: Event): void {
      const { shareType } = this.shareData;
      const { stat = '' } = this.infoData || {};
      const shareCardType = Number(shareType);
      // 根据表头类型发送埋点
      if (shareCardType === 3) {
        Weblog.log(Weblog.shareStat(stat).bt);
      } else if ([1, 2].includes(shareCardType)) {
        Weblog.log(Weblog.shareStat(stat).wencai);
      }
      // 跳转地址是客户端协议地址，pc 需要自己分割出来
      const url = this.shareData.url || '';
      let resultUrl = url;
      if (!url) {
        return;
      }
      // 如果本身需要跳转，就阻止冒泡，临时兜底方案，等type === 6的底层页做完要改回去
      event.stopPropagation();
      // 判断环境，如果是非同花顺APP内，则分割出地址
      if (!Application.environment.inTHSApp && url.indexOf('client:') !== -1) {
        resultUrl = url.substr(url.indexOf('url=') + 4);
      }
      if (shareCardType === 6 && Application.getAppInfo().platform === 'iphone') {
        window.location.href = url;
      }
      if (shareCardType === 7) {
        // 实盘认证需要使用全屏协议跳转
        Application.jumpClientPage(resultUrl);
      } else {
        Application.jumpPage(resultUrl);
      }
    },
  },
};
