 * @param {boolean} showStatus - 是否展示佩戴勋章弹窗
 */
const handleWearMedalPop = (showStatus: boolean): void => {
  if (showStatus) {
    window.medalScrollTop = document.documentElement.scrollTop || document.body.scrollTop;
    // 给 body 添加样式，防止滚动
    document.body.style.position = 'fixed';
    document.body.style.top = `-${window.medalScrollTop}px`;
  } else {
    // 恢复滚动
    document.body.style.position = '';
    document.body.style.top = '';
    // 恢复滚动位置
    document.documentElement.scrollTop = document.body.scrollTop = (window.medalScrollTop || 0) as number;
  }
  // 切换佩戴勋章弹窗的展示状态
  showWearMedalModal.value = showStatus;
};

/**
 * 佩戴/取消佩戴勋章
 * @param {MedalDetailData} medalItem - 勋章数据
 */
const handleOperateMedal = throttle(async (wearedMedalData: MedalDetailData[]): Promise<void> => {
  // 获取佩戴/取消佩戴勋章状态
  const action = wearedMedalData.length ? 'wear' : 'remove';
  // 获取佩戴/取消佩戴勋章文案
  const text = action === 'wear' ? '佩戴' : '取消佩戴';
  try {
    // 保存佩戴的勋章
    await operateMedal({
      uid: userid,
      medalId: action === 'wear' ? wearedMedalData[0].medalId : wearedMedalList.value[0].medalId,
      action,
    });
    Toast(`${text}勋章成功`);
    // 关闭佩戴勋章弹窗
    handleWearMedalPop(false);
    // 关闭勋章详情弹窗
    closeMedal();
    // 重新获取勋章墙数据
    getInitData(User.medalWallUserId);
  } catch (error) {
    Toast(`${text}勋章失败`);
  }
}, COMMON_NUMBER.THROTTLE_TIME);

/**
