<script>
export default {
  props: ['list', 'hideCloseIcon'],
  data() {
    return {
      selectIdx: -1,
    }
  },
  methods: {
    initShortcuts() {
      document.addEventListener("keydown", this.keydownProxy, true);
      this.$once('hook:beforeDestroy', () => {
        document.removeEventListener("keydown", this.keydownProxy, true);
      });
    },
    scroll(scrollTop) {
      this.$nextTick(() => {
        this.$refs.listRef.scrollTop = scrollTop;
      })
    },
    keydownProxy(e) {
      const listLen = this.list.length;
      if (e.key === "ArrowDown") {
        e.preventDefault();
        e.stopPropagation();
        if (listLen) {
          this.selectIdx = (this.selectIdx + 1) % listLen;
          this.scroll(this.selectIdx * 40);
        }
      }
      if (e.key === "ArrowUp") {
        e.preventDefault();
        e.stopPropagation();
        if (listLen) {
          this.selectIdx = (this.selectIdx - 1 + listLen) % listLen;
          this.scroll(this.selectIdx * 40);
        }
      }
      if (e.key === "Enter") {
        e.preventDefault();
        e.stopPropagation();
        if (this.selectIdx !== -1) {
          this.$emit('tagClick', this.list[this.selectIdx])
        }
      }
    },
  },
  watch: {
    list: {
      handler() {
        this.selectIdx = -1;
        this.$emit('changeHeight');
      },
      immediate: true
    }
  },
  mounted() {
    this.initShortcuts();
  }
}
</script>

