    // 新画线的兜底措施，因为是已图片形式展示，图片为空时，整个组件就不展示了
    noshowComponent(): boolean {
      return ['6'].includes(this.shareData.displayInfo.type) && !this.shareData?.displayInfo?.image;
    },
    // 是否展示副标题
    showSubTitle(): boolean {
      const { subTitle = '', type } = this.shareData.displayInfo;
      // 类型为 1， 2时才展示副标题
      return ['1', '2'].includes(type) && Boolean(subTitle);
    },
    // 是否展示右侧数据
    showShareData(): boolean {
      return this.showSubTitle && Boolean(this.shareData.displayInfo.data);
    },
    // 获取数据样式
    getDataClass(): string {
      // 赋予默认颜色样式
      let result = 'theme-text-title';
      const reg = /-?\d+(.\d+)?/;
      if (this.shareData?.displayInfo?.data) {
        const data = this.shareData.displayInfo.data.match(reg);
        if (data && data[0]) {
          // 为数据的情况下展示
          if (Number(data[0]) > 0) {
            result = 'share-number_inc';
            this.data = '+' + this.shareData.displayInfo.data;
          } else if (Number(data[0]) < 0) {
            result = 'share-number_des';
          }
        }
      }
      return result;
    },
    // 获取标题样式
    getTitleClass(): string {
      let result = '';
      // 这两种类型的卡片标题栏为两行
      const titleArray = ['3', '4'];
      if (titleArray.includes(this.shareData.displayInfo.type)) {
        result = 'share-title_lines';
      }
      return result;
    },
