/**
 * 获取勋章墙数据
 * @param {number} uid - 用户 id
 * @param {boolean} firstLoad - 是否第一次加载
 */
const getInitData = async (uid: number, firstLoad = false): Promise<void> => {
  try {
    // 获取勋章墙数据
    const res = await getMedalData({ uid: uid });
    // 初始化勋章墙数据
    medalWallData.value = res;
    // 初始化新版勋章数据
    newMedalList.value = res.listNew;
    // 初始化老版勋章数据
    oldMedalList.value = res.listOld;
    // 处理自己查看勋章墙时，获取已佩戴和可佩戴的勋章数据，并更新勋章状态
    handleSelfViewMedal();
    // 隐藏加载中
    loading.value = false;
    // 上报展示勋章墙
    window.weblog && window.weblog.report({
      id: 'ths_sns_medalPage',
      action: 'show',
      logmap: {
        showType: User.selfView ? 'my' : 'visitor',
      },
    });
  } catch {
    if (firstLoad) {
      // 隐藏加载中
      loading.value = false;
      // 第一次加载页面显示错误页面
      PageErrorStatus.value = firstLoad;
    } else {
      Toast('已佩戴勋章更新失败');
    }
  }
};

/**
 * 处理自己查看勋章墙时，获取已佩戴和可佩戴的勋章数据，并更新勋章状态
 */
const handleSelfViewMedal = (): void => {
  // 自己查看勋章墙时，才获取已佩戴和可佩戴的勋章数据，并更新勋章状态
  if (User.selfView) {
    // 重置可佩戴的勋章数据
    wearMedalList.value = [];
    // 重置已佩戴的勋章数据
    wearedMedalList.value = [];
    // 获取已佩戴和可佩戴的勋章数据
    newMedalList.value.forEach((categoryItem: NewMedalList) => {
      categoryItem?.medalSeriesList.forEach((medalItem: MedalDetailData) => {
        // 已佩戴的勋章和有可佩戴的勋章，分别存储
        if (medalItem.wearMedal) {
          wearedMedalList.value.push(medalItem);
        } else {
          // 已有的勋章，并且未过期，才能佩戴
          const medalUnexpired = medalItem?.expirationTime * COMMON_NUMBER.SECOND > new Date().getTime() || medalItem?.expirationTime === 0;
          if (medalItem.hasMedal && medalUnexpired) {
            wearMedalList.value.push(medalItem);
          }
        }
        // 新获得的勋章，需要更新勋章状态
        medalItem.newAcquired && updateMedalList.value.push(medalItem.medalId);
      });
    });
    // 更新勋章状态
    updateMedalStatus();
  }
};

/**
 * 更新勋章状态
 */
const updateMedalStatus = () => {
  // 没有新获得的勋章，不需要更新
  if (!updateMedalList.value.length) {
    return;
  }
  // 更新勋章状态
  try {
    updateMedal({
      uid: userid,
      medalIdList: updateMedalList.value.join(','),
    });
  } catch (error) {
    return;
  }
};

