 * @param {boolean} showStatus - 是否展示佩戴勋章弹窗
 */
const handleWearMedalPop = (showStatus: boolean): void => {
  if (showStatus) {
    window.medalScrollTop = document.documentElement.scrollTop || document.body.scrollTop;
    // 给 body 添加样式，防止滚动
    document.body.style.position = 'fixed';
    document.body.style.top = `-${window.medalScrollTop}px`;
  } else {
    // 恢复滚动
    document.body.style.position = '';
    document.body.style.top = '';
