      if (this.$refs.shareElement) {
        const serve = new IntersectionObserver(entries => {
          entries.forEach(item => {
            if (item.intersectionRatio > 0) {
              this.$emit('exposureFunction');
              serve.unobserve(this.$refs.shareElement as Element);
            }
          });
        });
        serve.observe(this.$refs.shareElement as Element);
