);

const addDataSource = (position?: number) => {
  if (position !== undefined) {
    selectedDataSourceList.value.splice(position + 1, 0, {
      id: uuid(),
      data: {
        dataSourceId: '',
        dataSourceName: '',
        fieldMappings: [],
      },
    });
