// 行选择操作
handleSelect(item, event) {
  let index = item.disIndex
  this.hideContextMenu()
  if (!this.disableMultiSelect && event && event.ctrlKey) {
    // ctrl多选
    if (!this.selectItems.includes(item)) {
      this.selectItems.push(item)
      if (index < this.selectIndexBegin) {
        this.selectIndexBegin = index
      }
    } else {
      this.selectItems = this.selectItems.filter(i => i !== item)
      if (index === this.selectIndexBegin) {
        this.selectIndexBegin = -1
        if (this.selectItems.length > 0) {
          this.selectIndexBegin = this.selectItems[0].disIndex
          this.selectItems.forEach(i => {
            if (i.disIndex < this.selectIndexBegin) {
              this.selectIndexBegin = i.disIndex
            }
          })
        }
      }
    }
  } else if (!this.disableMultiSelect && event && event.shiftKey) {
    // shift连续选择
    let startIndex = this.selectIndexBegin
    let endIndex = index
    if (endIndex < startIndex) {
      [startIndex, endIndex] = [endIndex, startIndex]
    }
    this.selectItems = this.listDis.slice(startIndex, endIndex + 1)
  } else {
    this.selectItems = [item]
    this.selectIndexBegin = index
  }
  if (this.watchSelect) {
    this.$emit('selectChange')
  }
