const store = inject<IStore>('store');
const { selectedDataSources, dataSourceUsedMap } = store as IStore;

const props = defineProps<{
  conditionGroupForDisplay: IConditionGroupForDisplay[];
  onEdit: () => void;
  disabled: boolean;
}>();

const valid = ref(true);

// 校验已使用的数据源是否异常
const verifyDataSource = () => {
  const value = props.conditionGroupForDisplay;

  let isValid = true;
  for (let i = 0; i < value.length; i++) {
    const group = value[i];
    if (!isValid) {
      break;
    }

    for (let j = 0; j < group.conditionList.length; j++) {
      const currentConditionGroup = group.conditionList[j];
      if (!isValid) {
        break;
      }

      for (let m = 0; m < currentConditionGroup.conditions.length; m++) {
        const innerCondition = currentConditionGroup.conditions[m];
        const { isInput } = innerCondition;
        innerCondition.targetType = isInput ? 'VALUE' : 'FIELD';
        // 判断过往映射中是否存在数据源不存在的情况，下同
        const sourceMapKey = `${group.id}_${currentConditionGroup.id}_${innerCondition.id}_sourceName`;
        const sourceValue = dataSourceUsedMap.value.find(d => d.key === sourceMapKey);
        if (sourceValue) {
          const selectedSource = selectedDataSources.value.find(d => d.id == sourceValue.value);
          if (!selectedSource) {
            isValid = false;
            break;
          }
        }

        if (!isInput) {
          const targetMapKey = `${group.id}_${currentConditionGroup.id}_${innerCondition.id}_targetName`;
          const sourceValue = dataSourceUsedMap.value.find(d => d.key === targetMapKey);
          if (sourceValue) {
            const selectedSource = selectedDataSources.value.find(d => d.id === sourceValue.value);
            if (!selectedSource) {
              isValid = false;
              break;
            }
          }
        }
      }
    }
  }

  valid.value = isValid;
};

watchEffect(() => {
  if (props.conditionGroupForDisplay) {
    verifyDataSource();
  }
});
</script>

