  // 
  .estool {
    width: 100%;
    height: 56px;
    display: flex;
    padding: 0 12px 0 16px;
    align-items: center;
    .loading {
      flex: none;
      width: 24px;
      height: 24px;
      background: url('~@/assets/images/command/icon_loading.svg') no-repeat;
      animation: wheel 1.5s linear infinite;
    }
    .loading-tip {
      flex: 1;
      font-size: 18px;
      color: #292933;
      line-height: 24px;
      font-weight: 500;
      margin: 0 8px;
    }
    .btn-stop {
      flex: none;
      height: 32px;
      border-radius: 16px;
      padding-left: 12px;
      border: none;
      box-shadow: none;
      .anticon {
        font-size: 15px;
        vertical-align: -2px;
      }
    }
    /deep/.btn-return {
      flex: none;
      height: 32px;
      border: 1px solid #E9E9F2;
      padding-left: 12px;
      border-radius: 16px;
      .anticon {
        transform: rotate(90deg);
      }
      > .anticon + span {
        margin-left: 4px;
      }
    }
  }
  // 
  .header {
    width: 100%;
    height: 56px;
    display: flex;
    padding: 0 12px 0 16px;
    align-items: center;
    justify-content: space-between;
    .select-tip {
      height: 24px;
      font-size: 18px;
      color: #292933;
      line-height: 24px;
      font-weight: 500;
      padding-left: 32px;
      background: url('~@/assets/images/command/icon_sure.svg') no-repeat;
      background-position: left center;
      background-size: 24px 24px;
    }
    .tool {
      .ant-btn {
        height: 32px;
        border-radius: 16px;
      }
      /deep/.btn-return {
        border: 1px solid #E9E9F2;
        padding-left: 12px;
        .anticon {
          transform: rotate(90deg);
        }
        > .anticon + span {
          margin-left: 4px;
        }
      }
      .btn-extract {
        background: #292933;
        margin-left: 8px;
        color: #FFFFFF;
        border: none;
      }
    }
  }
  .content {
    .list {
      border-top: 1px solid #E9E9F2;
      padding: 12px 16px;
      display: block;
      max-height: 400px;
      overflow-y: auto;
      .group {
        &+.group {
          margin-top: 20px;
        }
        .group-name {
          color: #232323;
          height: 16px;
          line-height: 16px;
          font-weight: 700;
          padding-left: 11px;
          position: relative;
          &:before {
            position: absolute;
            content: '';
            display: block;
            left: 0;
            top: 2px;
            width: 3px;
            height: 12px;
            background-image: linear-gradient(194deg, #32C5FF 0%, #B620E0 51%, #F7B500 100%);
            border-radius: 1.5px;
          }
        }
        .group-content {
          .item {
            height: 48px;
            line-height: 48px;
            background: rgba(255,255,255,0.70);
            border: 1px solid #ECECF7;
            box-shadow: 0px 8px 12px 0px rgba(0,36,104,0.06);
            border-radius: 8px;
            padding: 0 16px;
            color: #000000;
            margin-top: 12px;
            display: flex;
            align-items: center;
            .item-label {
              flex: 1;
            }
            .item-descript {
              flex: none;
              font-size: 14px;
              color: #87879C;
              margin-left: 16px;
            }
            .item-check {
              width: 16px;
              height: 16px;
              background: #292933;
              border-radius: 2px;
              position: relative;
              cursor: pointer;
              margin-left: 16px;
              &:before {
                position: absolute;
                content: '';
                left: 3px;
                top: 10px;
                width: 10px;
                height: 4px;
                border-left: 2px solid #FFFFFF;
                border-bottom: 2px solid #FFFFFF;
                transform: rotate(-45deg) scale(1);
                transform-origin: left top;
              }
            }
          }
        }
      }
    }
  }
}
