import NewsPage from '@/components/NewsPage/index.vue'
import ErrorPage from '@/components/Error/index.vue'
import { getNewsContentData, getRecommendData, DEFAULT_LANG, getAIQuery, getFlashNewsId } from '@/common/api'
import { useRoute } from '@/router'
import { ref, Component, computed, onMounted, onBeforeMount } from 'vue'
import NavBar from '@/components/NewsPage/NavBar.vue'
import { isArray, get } from 'lodash'
import { ContentData, RecommendInfo, RecommendInfoCom } from '@/common/api/interface'
import { isInApp, getLang } from '@/common/utils'
import { showStat, statConfig } from '@/common/utils/stat'
import LoadingSkeleton from '@/components/NewsPage/LoadingSkeleton.vue'
import useCharge from '@/common/hooks/useCharge'
import fl from '@king-fisher/falcon'
import { checkIphoneX } from '@/common/utils/checkIphoneX'
import EventBus from '@/common/utils/eventBus'

const { setChargedStatus } = useCharge()
const query = useRoute().query
const { id, lang, source } = query

const isIOS = window.getPlatform() === 'iphone'
const isIphoneX = checkIphoneX()
const contentData = ref({} as ContentData)
const aiAskData = ref<any>([])
//推荐数据
const recommendItem = ref<Array<RecommendInfoCom>>([] as Array<RecommendInfoCom>)

const componentEnum = {
  'NewsPage': NewsPage,
  'ErrorPage': ErrorPage
}
const currentComponent = ref<Component>(LoadingSkeleton)
const checkRedirectSource = contentData => {
  const REDIRECT_TYPE = 2
  const { pageInfo = {} } = contentData
  const { jumpType, sourceUrl } = pageInfo
  const redirectStatus = jumpType === REDIRECT_TYPE
  return {
    redirectStatus,
    sourceUrl,
  }
}

// 获取推荐列表
const getCurRecommendData = async () => {
  const {
    data: { recommendInfo },
  } = await getRecommendData(id as string, {
    platform: 'app'
  })
  if (!isArray(recommendInfo)) {
    return
  }
  const tempItem = recommendInfo.map((d: RecommendInfo) => {
    // 如果当前路由上已经存在lang字符,那么再处理相关阅读推荐的时候,需要将当前字段透传至下一个底层页
    if (lang) {
      d.url = `${d.url}&lang=${lang}`
    }
    return {
      ...d,
      source: d.media.name || '',
    }
  })
  recommendItem.value = tempItem
}
//获取文章信息
const getContent = async (lang: string = DEFAULT_LANG) => {
  try {
    const result = await getNewsContentData(id as string, lang);
    contentData.value = result?.data ?? {}
    const { contentInfo } = contentData.value
    // 获取咨询的真正id
    const contentId = get(result, 'data.contentInfo.id', '')
    if (contentId) {
      getAIQueryInfo(contentId)
      handleAIme(contentId, lang)
    }

    //是否有版权
    const redirectResult = checkRedirectSource(contentData.value);
    // 如果内容不存在，返回情况
    if (!contentInfo) {
      currentComponent.value = componentEnum['ErrorPage']
      return
    }
    if (redirectResult.redirectStatus) {
      window.location.replace(redirectResult.sourceUrl)
      return
    }
    // 校验当前付费状态
    await setChargedStatus(contentData.value)

    currentComponent.value = componentEnum['NewsPage']
  } catch (e) {
    // 失败主动上报,此处主要针对的是接口不走端内后的失败采集
    window.ClientMonitor?.reportFrameErrors({
      category: 'promise',
      grade: 'Info'
    },
      {
        name: '获取主内容接口异常',
        message: JSON.stringify(e),
        stack: '-'
      })
    currentComponent.value = componentEnum['ErrorPage']
  }
}
const getNavData = computed(() => {
  const contentInfo = contentData.value.contentInfo;
  if (contentInfo) {
    const { id: newsId, title, coverImage, type, businessCode } = contentInfo;
    return {
      id: newsId,
      title,
      coverImage,
      type,
      businessCode
    }
  }
  return {
    id: '',
    title: '',
  };
})

// 获取内容, 由于长文底层页的多语言需要跟随要闻列表,所以此处需要区分如果链接上存在lang字段那么直接通过lang字段设置多语言
if (lang) {
  getContent(lang as string)
} else {
  if (isInApp) {
    window.callNativeHandler('webLocalStorage', {
      type: 'get',
      key: 'news-flash-news-lang',
    }, ({ data }) => {
      // 以APP的语言进行默认兜底
      const defaultLang = getLang();
      const lang = data?.value || defaultLang;
      getContent(lang);
    });
  } else {
    getContent();
  }
}

// 获取当前的推荐内容
getCurRecommendData()

// 获取底层页的AI问答数据
const getAIQueryInfo = async (id: string) => {
  const flashNewsData: any = await getFlashNewsId(id)
  const newsId = flashNewsData?.data?.newsId
  if (!newsId) return

  const { data } = await getAIQuery([newsId])
  if (data[newsId]) {
    const list = data[newsId] || []
    const queryAndAnswer = list.map((ele, index) => {
      return {
        id: index,
        ...ele.itemInfo
      }
    })
    aiAskData.value = queryAndAnswer
  }
}

// 展示底部AIME
const showAiMe = () => {
  const isIos = /iPhone|iPad/.test(navigator.userAgent)
  if (!isIos) {
    return
  }
  fl.showAnyWhereAIme({});
}

// 隐藏底部AIME
const hideAiMe = () => {
  fl.hideAnyWhereAIme({});
}

// 处理AIme无处不在的弹窗
const handleAIme = (id: string, lang: string) => {
  const isIos = /iPhone|iPad/.test(navigator.userAgent)
  if (!isIos) {
    return
  }

  fl.showAnyWhereAIme({})

  const params = {
    lang,
    from: 'in-app',
    funcCards: [{
      newsId: id
    }]
  }
  fl.callAImeQuery({
    ...params,
  })

  // iphoneX 底部预留高度
  const IPHONEX_SPACE = 34
  fl.updateAnyWhereAImeSafeAreaInsets({
    top: 0,
    // 处理aime气泡位置,防止遮挡底层页底部的交互
    bottom: isIphoneX ? IPHONEX_SPACE : 0,
    left: 0,
    right: 0,
  })
}

const hanldeAImeStatus = (status: boolean) => {
  status ? showAiMe() : hideAiMe()
}

onMounted(() => {
  // 记录当前页面的uv埋点
  showStat(statConfig.pageExposure, {
    news: id,
    from: source || 'inapp'
  })
  EventBus.$on('handleAImeStatus', hanldeAImeStatus)
})

onBeforeMount(() => {
  EventBus.$off('handleAImeStatus')
})

</script>

