async function handleOnlyQuoteClick() {
  sharedState.$patch(state => {
    state.onlyQuotes = !state.onlyQuotes;
  });
  await getTableData();
  setDefaultCurrentRow();
}
/** 设置默认选中的行 */
function setDefaultCurrentRow() {
  const currentRow = tableDataSource[sharedState.currentTableType].value[0] || null;
  sharedState.$patch(state => {
    state.currentRow = currentRow;
    stkTableRef.value?.setCurrentRow(currentRow.isin);
  });
}
async function handleTableTypeChange() {
  await getTableData();
  setDefaultCurrentRow();
}

/** 更新表格数据 */
async function getTableData() {
  if (sharedState.currentTableType === TableTypeTab.Deal) {
    const data = await getRealTimeDealData();
    tableDataSource[TableTypeTab.Deal].value = data;
  } else {
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const a: TableTypeTab.Quote = sharedState.currentTableType; // ts-check
    const data = await getOffshoreQuoteData();
    tableDataSource[TableTypeTab.Quote].value = data.list;
  }
}

/** 获取实时成交数据 */
function getRealTimeDealData() {
  loading.value = true;
  return new Promise<any[]>(resolve => {
    window.setTimeout(() => {
      const data = Array.from({ length: 200 }).map((v, i) => {
        return {
          id: i,
        };
      });
      resolve(data);
    }, 1000);
  }).finally(() => {
    loading.value = false;
  });
}

/** 获取离岸债报价 */
function getOffshoreQuoteData() {
  loading.value = true;
  return getOffshoreQuote().finally(() => {
    loading.value = false;
  });
}

/**表头右键处理 */
function handleHeaderRowMenu(e: MouseEvent) {
  if (sharedState.currentTableMode === TableMode.Easy) return; // 简易模式没有右键
  headerMenuConfig[sharedState.currentTableType]?.show(e);
}

/**快速筛选 */
function openQuickFilter() {
  quickFilterRef.value?.show();
}

function handleFilterConfirm() {
  getTableData();
}

/** 表格排序变化 */
function handleSortChange(col: StkTableColumn<OffshoreQuotationData>, order: Order) {
  sharedState.$patch(state => {
    state.sortState[state.currentTableType] = {
      sortField: col.sortField || col.dataIndex,
      order,
    };
  });

  getTableData();
}

/** 表格当前行变化 */
function handleCurrentChange(e: MouseEvent | null, row: OffshoreQuotationData) {
  sharedState.$patch(state => {
    state.currentRow = row;
  });
  GlobalEventBus.dispatchEvent(GlobalEventBus.EventName.MainGridCurrentRowChange, { row });
}
